mapper.select:
select users.user_id, user_name, reg_date, post_title, post_content from users join posts on users.user_id = posts.user_id where users.user_name = #{userName}
and post_title like '%'||#{post.title}||'%'
and post_content like '%'||#{post.content}||'%'

mapper.update:
update users
user_name=#{userName}, reg_date=#{regDate},
where user_id = #{userId}

mapper.select:
select user_id userId, user_name userName, reg_date regDate
from users

   where user_name =#{userName}
